class Find
{
	def log;
	def context;
	def testRunner;	

	def Find(log,context,testRunner)
	{

		this.log = log; 
		this.context = context;
		this.testRunner = testRunner;
	}
	def Samir(int a,int b,int c)
	{
		int max;
		if (a>b && a>c)
		{
		max = a;
		log.info " $max is the greatest number of all three " ; 
		}
		else if (b>a && b>c)
		{
		max = b;
		log.info " $max is the greatest number of all three " ;
		}
		else if (c>a && c>b)
		{
		max = c;
		log.info " $max is the greatest number of all three " ;
		}
		else
		{
		log.info " All the Numbers are Equal " ;
		}
	}
}

